import React, { Component } from "react";
import "./App.css";

const PLACES = [
  { name: "Moscow", zip: "119048" },
  { name: "Saint Petersburg", zip: "190000" },
  { name: "Reims", zip: "08000" },
  { name: "Barcelona", zip: "08002" }
];

class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null
    };
  }

  componentDidMount() {
    const zip = this.props.zip;
    const URL = `http://api.openweathermap.org/data/2.5/weather?zip=${zip}&appid=b1b35bba8b434a28a0be2a3e1071ae5b&units=imperial`;
    fetch(URL)
      .then(res => res.json())
      .then(json => {
        this.setState({
          weatherData: json
        });
      })
      .catch(() => {
        alert("error on the get | just believe me it's a bullshit br");
      });
  }

  render() {
    const weatherData = this.state.weatherData;
    if (!weatherData) return <div className="wait">Wait please one second</div>;
    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";
    return (
      <div className="container">
        <h1 className="h1">
          {weather.main} in {weatherData.name}
          <img src={iconUrl} alt={weather.description} />
        </h1>
        <div className="resualt">
          <p>Текущая: {(Math.round((weatherData.main.temp - 32) / 1.8))}°</p>
          <p>Макс: {(Math.round((weatherData.main.temp_max - 32) / 1.8))}°</p>
          <p>Мин: {(Math.round((weatherData.main.temp_min - 32) / 1.8))}°</p>
          <p>Скорость ветра: {Math.round(weatherData.wind.speed)} миль/час</p>
        </div>
      </div>
    );
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      activePlace: 0,
      userZip: "" // Состояние для хранения ZIP-кода пользователя
    };
  }

  handleZipChange = (event) => {
    this.setState({ userZip: event.target.value });
  }

  render() {
    const { activePlace, userZip } = this.state;

    return (
      <div className="App">
        {PLACES.map((place, index) => (
          <button
            key={index}
            onClick={() => {
              this.setState({ activePlace: index, userZip: "" }); // Сброс пользовательского ZIP-кода при выборе места
            }}
          >
            {place.name}
          </button>
        ))}

        <input
          type="text"
          placeholder="Введите свой ZIP-код"
          value={userZip}
          onChange={this.handleZipChange}
        />
        <button
          onClick={() => {
            if (userZip) {
              this.setState({ activePlace: -1 }); // Обновляем активное место для отображения пользовательского ZIP-кода
            }
          }}
        >
          Получить погоду по ZIP-коду
        </button>

        <WeatherDisplay key={activePlace} zip={userZip || PLACES[activePlace].zip} />
      </div>
    );
  }
}

export default App;
